class Solution {
public:
	int longestValidParentheses(string s) {
		if (s.length() <= 1)
			return 0;
		int res = 0;
		vector<int> dp(s.length(), 0);
		for (int i = 1; i < s.length(); i++){
			if (s[i] == '(')
				continue;
			if (s[i - 1] == '('){
				dp[i] = 2 + (i - 2 >= 0 ? dp[i - 2] : 0);
			}
			else{
				if (i - 1 - dp[i - 1] >= 0 && s[i - 1 - dp[i - 1]] == '('){
					dp[i] = 2 + dp[i - 1] + (i - dp[i - 1] - 2 >= 0 ? dp[i - dp[i - 1] - 2] : 0);
				}
			}
			res = max(res, dp[i]);
		}
		return res;
	}
};


