//Solution4 : greedy and priority_queue -- AC nlogn
class Solution {
public:
	int minRefuelStops(int target, int startFuel, vector<vector<int>>& stations) {
		int N = stations.size();
		int dist = startFuel;// 能行驶的最大距离
		int index = 0;
		int res = 0;
		priority_queue<int> pq;
		// 贪婪法
		// 油量够时不加油，只把油量记入堆中；油量不够时，从堆中取最大值加油
		while (true){
			if (dist >= target)
				return res;
			while (index < N && dist >= stations[index][0]){
				pq.push(stations[index][1]);
				index++;
			}
			if (pq.empty())// 无油可加
				return -1;
			int fuel = pq.top();
			pq.pop();
			dist += fuel;
			res++;
		}
		return -1;
	}
};
